var a=Object.defineProperty;var t=(o,e)=>a(o,"name",{value:e,configurable:!0});import{_ as i,c as d,o as l,ai as c}from"./chunks/framework.BnOgjZe9.js";const f=JSON.parse('{"title":"API 概览","description":"","frontmatter":{"title":"API 概览","sidebar_position":1},"headers":[],"relativePath":"docs/api/preview.md","filePath":"docs/api/preview.md"}'),r={name:"docs/api/preview.md"};function n(o,e,s,h,u,p){return l(),d("div",null,[...e[0]||(e[0]=[c('<h1 id="api-概览" tabindex="-1">API 概览 <a class="header-anchor" href="#api-概览" aria-label="Permalink to &quot;API 概览&quot;">​</a></h1><h2 id="核心类" tabindex="-1">核心类 <a class="header-anchor" href="#核心类" aria-label="Permalink to &quot;核心类&quot;">​</a></h2><h3 id="widget" tabindex="-1">Widget <a class="header-anchor" href="#widget" aria-label="Permalink to &quot;Widget&quot;">​</a></h3><p>Inkwell 的核心构建单元。与 Flutter 不同，Inkwell 采用单树结构，Widget 同时承载配置、状态和渲染逻辑。</p><ul><li><code>createElement(data)</code>: 更新属性并（按需）重建子树（包含子节点 diff/复用）。</li><li><code>layout(constraints)</code>: 执行布局，写入 <code>renderObject.size</code>，并在尺寸确定后定位子节点。</li><li><code>paint(context)</code>: 处理透明度级联、脏区域剔除与离屏渲染（RepaintBoundary），然后绘制自身与子节点。</li><li><code>hitTest(x, y)</code>: 判断坐标是否命中当前节点（返回 boolean）。</li><li><code>visitHitTest(x, y)</code>: 递归命中测试并返回最上层可响应的节点（或 null）。</li></ul><h3 id="statelesswidget" tabindex="-1">StatelessWidget <a class="header-anchor" href="#statelesswidget" aria-label="Permalink to &quot;StatelessWidget&quot;">​</a></h3><p>无状态复合组件：通过 <code>render()</code> 返回 JSX/Element，运行时会将其编译为子节点数据并挂载到当前节点下（通常只有一个根子节点）。</p><ul><li><code>render()</code>: 返回需要渲染的子树描述。</li></ul><h3 id="statefulwidget" tabindex="-1">StatefulWidget <a class="header-anchor" href="#statefulwidget" aria-label="Permalink to &quot;StatefulWidget&quot;">​</a></h3><p>有状态复合组件：在 <code>StatelessWidget</code> 的基础上增加 <code>state</code> 与状态更新机制。</p><h3 id="state" tabindex="-1">State <a class="header-anchor" href="#state" aria-label="Permalink to &quot;State&quot;">​</a></h3><p><code>StatefulWidget</code> 的逻辑和状态持有者。</p><ul><li><code>setState(partial)</code>: 合并更新状态并触发重建/重绘。</li><li><code>didUpdateWidget(oldProps)</code>: 当组件配置更新时调用（用于同步 state）。</li><li><code>dispose()</code>: 销毁时清理资源。</li></ul><h3 id="buildcontext" tabindex="-1">BuildContext <a class="header-anchor" href="#buildcontext" aria-label="Permalink to &quot;BuildContext&quot;">​</a></h3><p>绘制上下文：主要提供渲染器与当前世界矩阵等信息，供 <code>paint/paintSelf</code> 阶段使用。</p><ul><li><code>renderer</code>: 渲染器实例。</li><li><code>worldMatrix</code>: 当前节点的世界变换矩阵（可选）。</li><li><code>dirtyRect</code>: 当前帧脏矩形区域（可选）。</li><li><code>enableOffscreenRendering</code>: 是否启用离屏渲染（可选）。</li><li><code>opacity</code>: 当前级联透明度（可选）。</li></ul><h3 id="boxconstraints" tabindex="-1">BoxConstraints <a class="header-anchor" href="#boxconstraints" aria-label="Permalink to &quot;BoxConstraints&quot;">​</a></h3><p>布局约束，定义了组件的最小和最大宽高。</p><ul><li><code>minWidth</code>, <code>maxWidth</code></li><li><code>minHeight</code>, <code>maxHeight</code></li></ul><h3 id="size" tabindex="-1">Size <a class="header-anchor" href="#size" aria-label="Permalink to &quot;Size&quot;">​</a></h3><p>组件的大小。</p><ul><li><code>width</code>, <code>height</code></li></ul><h2 id="常用-widget" tabindex="-1">常用 Widget <a class="header-anchor" href="#常用-widget" aria-label="Permalink to &quot;常用 Widget&quot;">​</a></h2><h3 id="布局-layout" tabindex="-1">布局 (Layout) <a class="header-anchor" href="#布局-layout" aria-label="Permalink to &quot;布局 (Layout)&quot;">​</a></h3><ul><li><code>Container</code>: 组合绘制、定位、尺寸调整的便捷组件。</li><li><code>Row</code> / <code>Column</code>: Flex 布局组件，支持 <code>mainAxisAlignment</code> 和 <code>crossAxisAlignment</code>。</li><li><code>Stack</code>: 层叠布局组件，配合 <code>Positioned</code> 使用。</li><li><code>Positioned</code>: 在 Stack 中定位子组件。</li><li><code>Expanded</code>: 在 Row/Column 中填充剩余空间。</li><li><code>Wrap</code>: 流式布局组件。</li><li><code>Viewport</code> / <code>ScrollView</code>: 滚动视图组件。</li></ul><h3 id="内容-content" tabindex="-1">内容 (Content) <a class="header-anchor" href="#内容-content" aria-label="Permalink to &quot;内容 (Content)&quot;">​</a></h3><ul><li><code>Text</code>: 文本渲染，支持 <code>fontSize</code>, <code>color</code>, <code>fontWeight</code> 等样式。</li><li><code>Image</code>: 图片渲染，支持 <code>src</code>, <code>fit</code> 等属性。</li></ul><h3 id="辅助-helper" tabindex="-1">辅助 (Helper) <a class="header-anchor" href="#辅助-helper" aria-label="Permalink to &quot;辅助 (Helper)&quot;">​</a></h3><ul><li><code>Padding</code>: 添加内边距。</li><li><code>Center</code>: 居中对齐子组件。</li><li><code>SizedBox</code>: 强制指定大小。</li></ul>',29)])])}t(n,"_sfc_render");const m=i(r,[["render",n]]);export{f as __pageData,m as default};
